import React from 'react';
import { Card } from '../types/types';
import './GameBoard.css';

interface GameBoardProps {
  board: Card[];
  selectedCards: Card[];
  possibleCombinations: Card[][];
  onCardClick: (card: Card) => void;
}

const GameBoard: React.FC<GameBoardProps> = ({ 
  board, 
  selectedCards, 
  possibleCombinations,
  onCardClick 
}) => {
  // 3x3 보드에 필요한 9개 카드만 사용
  const boardCards = board.slice(0, 9);

  // 종족, 직업, 성향 한글 변환 함수
  const getRaceText = (race: Card['race']): string => {
    const raceMap: Record<Card['race'], string> = {
      'human': '인간',
      'elf': '엘프',
      'dwarf': '드워프'
    };
    return raceMap[race];
  };

  const getJobText = (job: Card['job']): string => {
    const jobMap: Record<Card['job'], string> = {
      'warrior': '전사',
      'mage': '마법사',
      'archer': '궁수'
    };
    return jobMap[job];
  };

  const getAlignmentText = (alignment: Card['alignment']): string => {
    const alignmentMap: Record<Card['alignment'], string> = {
      'cold': '냉철',
      'order': '정렬',
      'chaos': '혼돈'
    };
    return alignmentMap[alignment];
  };

  // 카드가 가능한 조합에 속하는지 확인
  const isCardInPossibleCombination = (card: Card): boolean => {
    if (possibleCombinations.length === 0) return false;

    // 첫 번째 가능한 조합에서만 체크
    return possibleCombinations[0].some(c => c.id === card.id);
  };

  return (
    <div className="game-board">
      {boardCards.map((card) => {
        const isSelected = selectedCards.some(c => c.id === card.id);
        const isPossible = isCardInPossibleCombination(card);

        return (
          <div 
            key={card.id} 
            className={`card ${isSelected ? 'selected' : ''} ${isPossible ? 'possible' : ''}`}
            onClick={() => onCardClick(card)}
          >
            <img 
              src={card.imagePath} 
              alt={`${card.race} ${card.job} (${card.alignment})`} 
            />
            <div className="card-info">
              <div className="card-summary">
                {`${getRaceText(card.race)} ${getJobText(card.job)} (${getAlignmentText(card.alignment)})`}
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default GameBoard;
