    setSelectedCards([]);
    setComputerSelectedCards([]);
    setPossibleCombinations([]);
    setFadingCards([]);
    isProcessingRef.current = false;

    // Í∞úÎ∞ú ÎîîÎ≤ÑÍπÖÏö©: Ï§ëÎ≥µ Ïπ¥Îìú Ï≤¥ÌÅ¨
    const boardIds = initialBoard.map(card => card.id);
    const uniqueIds = new Set(boardIds);
    if (uniqueIds.size !== boardIds.length) {
      console.error('Ï¥àÍ∏∞ Î≥¥ÎìúÏóê Ï§ëÎ≥µÎêú Ïπ¥ÎìúÍ∞Ä ÏûàÏäµÎãàÎã§!', boardIds);
    }

    // Ïª¥Ìì®ÌÑ∞ ÏÇ¨Í≥† ÏãúÏûë
    setIsComputerThinking(true);

    // Ïª¥Ìì®ÌÑ∞Ïùò ÏùëÎãµ ÏãúÍ∞ÑÏùÑ 10~20Ï¥à ÏÇ¨Ïù¥Î°ú ÎûúÎç§ ÏÑ§Ï†ï
    const computerDelay = 10000 + Math.random() * 10000;

    computerTimerRef.current = setTimeout(() => {
      if (!isProcessingRef.current) {
        computerTurn();
      }
    }, computerDelay);
  }, [usedCardIds]);

  // 3Ïû•Ïùò Ïπ¥ÎìúÍ∞Ä ÏÑ†ÌÉùÎê† ÎïåÎßàÎã§ ÏûêÎèôÏúºÎ°ú Ïú†Ìö®ÏÑ± Í≤ÄÏ¶ù
  useEffect(() => {
    if (selectedCards.length === 3 && !isProcessingRef.current) {
      validateSelection();
    }
  }, [selectedCards]);

  // Ïª¥Ìì®ÌÑ∞ ÏÑ†ÌÉù Ïπ¥Îìú Í≤ÄÏ¶ù
  useEffect(() => {
    if (computerSelectedCards.length === 3 && !isProcessingRef.current) {
      validateComputerSelection();
    }
  }, [computerSelectedCards]);

  // Í∞ÄÎä•Ìïú Î™®Îì† 3Ïû• Ï°∞Ìï©ÏùÑ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò
  const findAllValidCombinations = useCallback((): Card[][] => {
    const boardCards = board.slice(0, 9);
    const validCombinations: Card[][] = [];

    // Î™®Îì† Í∞ÄÎä•Ìïú 3Ïû• Ï°∞Ìï© ÏÉùÏÑ±
    for (let i = 0; i < boardCards.length; i++) {
      for (let j = i + 1; j < boardCards.length; j++) {
        for (let k = j + 1; k < boardCards.length; k++) {
          const combination = [boardCards[i], boardCards[j], boardCards[k]];
          // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù∏ÏßÄ ÌôïÏù∏
          if (compareAttributes(combination).isValid) {
            validCombinations.push(combination);
          }
        }
      }
    }

    return validCombinations;
  }, [board]);

  // Ïª¥Ìì®ÌÑ∞ ÌÑ¥ Ï≤òÎ¶¨ Ìï®Ïàò
  const computerTurn = useCallback(() => {
    if (isProcessingRef.current || fadingCards.length > 0) return;

    const validCombinations = findAllValidCombinations();

    if (validCombinations.length > 0) {
      // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏûàÏùÑ Í≤ΩÏö∞ ÎûúÎç§ÏúºÎ°ú ÌïòÎÇò ÏÑ†ÌÉù
      const randomIndex = Math.floor(Math.random() * validCombinations.length);
      const selectedCombination = validCombinations[randomIndex];

      // ÏÑ∏ Ïû•Ïùò Ïπ¥ÎìúÎ•º ÌïúÍ∫ºÎ≤àÏóê ÏÑ†ÌÉù (Ïª¥Ìì®ÌÑ∞Îäî Ï¶âÏãú ÏÑ†ÌÉù)
      setComputerSelectedCards(selectedCombination);
    } else {
      // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÏùÑ Í≤ΩÏö∞ NO TEAM ÏÑ†ÌÉù
      handleComputerNoTeam();
    }

    setIsComputerThinking(false);
  }, [board, fadingCards, findAllValidCombinations]);

  // Ïª¥Ìì®ÌÑ∞Ïùò NO TEAM ÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleComputerNoTeam = useCallback(() => {
    if (isProcessingRef.current) return;
    isProcessingRef.current = true;

    // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÎäî Í≤ΩÏö∞ - Ï†êÏàò Ï∂îÍ∞Ä Î∞è Î≥¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
    setComputerScore(prevScore => prevScore + 10);
    setToast({
      isVisible: true,
      type: 'error',
      message: 'Ïª¥Ìì®ÌÑ∞Í∞Ä NO TEAMÏùÑ ÏÑ†ÌÉùÌñàÏäµÎãàÎã§! +10Ï†ê'
    });

    // Î™®Îì† Ïπ¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
    const boardCardIds = board.slice(0, 9).map(card => card.id);

    // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ Î™®Îì† Ïπ¥Îìú ID Ï†ÄÏû•
    setFadingCards(boardCardIds);

    // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
    setTimeout(() => {
      // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
      setUsedCardIds(prev => [...prev, ...boardCardIds]);

      // ÏÉà ÎùºÏö¥Îìú ÏãúÏûë
      startNewRound();
    }, 600);
  }, [board, startNewRound]);

  // Ïª¥Ìì®ÌÑ∞ ÏÑ†ÌÉù Í≤ÄÏ¶ù
  const validateComputerSelection = useCallback(() => {
    if (computerSelectedCards.length === 3) {
      if (isProcessingRef.current) return;
      isProcessingRef.current = true;

      const result = compareAttributes(computerSelectedCards);

      if (result.isValid) {
        // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïùº Í≤ΩÏö∞
        setComputerScore(prevScore => prevScore + 1);

        // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú
        setToast({
          isVisible: true,
          type: 'error',
          message: 'Ïª¥Ìì®ÌÑ∞Í∞Ä Ï°∞Ìï©ÏùÑ Ï∞æÏïòÏäµÎãàÎã§! +1Ï†ê'
        });

        // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
        const selectedIds = computerSelectedCards.map(card => card.id);
        setUsedCardIds(prev => [...prev, ...selectedIds]);

        // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ ÏÑ†ÌÉùÎêú Ïπ¥Îìú ID Ï†ÄÏû•
        setFadingCards(selectedIds);

        // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
        setTimeout(() => {
          // ÏÑ†ÌÉùÎêú Ïπ¥Îìú ÍµêÏ≤¥
          const newBoard = refreshBoard(board, selectedIds, usedCardIds);
          setBoard(newBoard);
          // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º Ìï¥Ï†ú
          setFadingCards([]);
          // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
          setComputerSelectedCards([]);
          setSelectedCards([]);
          // Ï≤òÎ¶¨ ÏÉÅÌÉú Ìï¥Ï†ú
          isProcessingRef.current = false;

          // Ïª¥Ìì®ÌÑ∞ Îã§Ïùå ÏÉùÍ∞Å ÏãúÏûë (10~20Ï¥à)
          setIsComputerThinking(true);
          const computerDelay = 10000 + Math.random() * 10000;
          computerTimerRef.current = setTimeout(() => {
            if (!isProcessingRef.current) {
              computerTurn();
            }
          }, computerDelay);

          // Í∞úÎ∞ú ÎîîÎ≤ÑÍπÖÏö©: Ï§ëÎ≥µ Ïπ¥Îìú Ï≤¥ÌÅ¨
          const newBoardIds = newBoard.map(card => card.id);
          const uniqueIds = new Set(newBoardIds);
          if (uniqueIds.size !== newBoardIds.length) {
            console.error('Ïª¥Ìì®ÌÑ∞ ÌÑ¥ ÌõÑ ÏÉà Î≥¥ÎìúÏóê Ï§ëÎ≥µÎêú Ïπ¥ÎìúÍ∞Ä ÏûàÏäµÎãàÎã§!', newBoardIds);
          }
        }, 600);
      }
    }
  }, [computerSelectedCards, board, usedCardIds, computerTurn, startNewRound]);

  const handleCardSelect = (card: Card) => {
    if (fadingCards.length > 0 || isProcessingRef.current) return; // Ïπ¥Îìú ÌéòÏù¥ÎìúÏïÑÏõÉ Ï§ëÏù¥ÎÇò Ï≤òÎ¶¨ Ï§ëÏù¥Î©¥ ÏÑ†ÌÉù Î∂àÍ∞Ä

    if (selectedCards.some(c => c.id === card.id)) {
      // Ïù¥ÎØ∏ ÏÑ†ÌÉùÎêú Ïπ¥ÎìúÎ©¥ ÏÑ†ÌÉù Ï∑®ÏÜå
      setSelectedCards(selectedCards.filter(c => c.id !== card.id));
    } else if (selectedCards.length < 3) {
      // 3Ïû• ÎØ∏ÎßåÏù¥Î©¥ ÏÑ†ÌÉù
      setSelectedCards([...selectedCards, card]);
    }
  };

  const validateSelection = () => {
    if (selectedCards.length === 3) {
      if (isProcessingRef.current) return;
      isProcessingRef.current = true;

      // Ïù¥Ï†Ñ Ïª¥Ìì®ÌÑ∞ ÌÉÄÏù¥Î®∏ Ï∑®ÏÜå
      if (computerTimerRef.current) {
        clearTimeout(computerTimerRef.current);
        computerTimerRef.current = null;
      }

      const result = compareAttributes(selectedCards);
      setValidationResult(result);

      if (result.isValid) {
        // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïùº Í≤ΩÏö∞
        setPlayerScore(prevScore => prevScore + 1);

        // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
        const selectedIds = selectedCards.map(card => card.id);
        setUsedCardIds(prev => [...prev, ...selectedIds]);

        // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú
        setToast({
          isVisible: true,
          type: 'success',
          message: 'Ïú†Ìö®Ìïú Ï°∞Ìï©ÏûÖÎãàÎã§! +1Ï†ê'
        });

        // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ ÏÑ†ÌÉùÎêú Ïπ¥Îìú ID Ï†ÄÏû•
        setFadingCards(selectedIds);

        // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
        setTimeout(() => {
          // ÏÑ†ÌÉùÎêú Ïπ¥Îìú ÍµêÏ≤¥
          const newBoard = refreshBoard(board, selectedIds, usedCardIds);
          setBoard(newBoard);
          // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º Ìï¥Ï†ú
          setFadingCards([]);
          // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
          setSelectedCards([]);
          setComputerSelectedCards([]);
          // Ï≤òÎ¶¨ ÏÉÅÌÉú Ìï¥Ï†ú
          isProcessingRef.current = false;
          // Í∞ÄÎä•Ìïú Ï°∞Ìï© Ï¥àÍ∏∞Ìôî
          setPossibleCombinations([]);

          // Ïª¥Ìì®ÌÑ∞ Îã§Ïùå ÏÉùÍ∞Å ÏãúÏûë (10~20Ï¥à)
          setIsComputerThinking(true);
          const computerDelay = 10000 + Math.random() * 10000;
          computerTimerRef.current = setTimeout(() => {
            if (!isProcessingRef.current) {
              computerTurn();
            }
          }, computerDelay);

          // Í∞úÎ∞ú ÎîîÎ≤ÑÍπÖÏö©: Ï§ëÎ≥µ Ïπ¥Îìú Ï≤¥ÌÅ¨
          const newBoardIds = newBoard.map(card => card.id);
          const uniqueIds = new Set(newBoardIds);
          if (uniqueIds.size !== newBoardIds.length) {
            console.error('ÌîåÎ†àÏù¥Ïñ¥ ÌÑ¥ ÌõÑ ÏÉà Î≥¥ÎìúÏóê Ï§ëÎ≥µÎêú Ïπ¥ÎìúÍ∞Ä ÏûàÏäµÎãàÎã§!', newBoardIds);
          }
        }, 600);
      } else {
        // Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ Ï°∞Ìï©Ïùº Í≤ΩÏö∞
        setToast({
          isVisible: true,
          type: 'error',
          message: 'Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ Ï°∞Ìï©ÏûÖÎãàÎã§.'
        });

        // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
        setSelectedCards([]);
        isProcessingRef.current = false;
      }
    }
  };

  // NO TEAM Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleNoTeam = () => {
    if (fadingCards.length > 0 || isProcessingRef.current) return; // Ïπ¥Îìú ÌéòÏù¥ÎìúÏïÑÏõÉ Ï§ëÏù¥ÎÇò Ï≤òÎ¶¨ Ï§ëÏù¥Î©¥ ÎèôÏûë Î∂àÍ∞Ä
    isProcessingRef.current = true;

    // Ïù¥Ï†Ñ Ïª¥Ìì®ÌÑ∞ ÌÉÄÏù¥Î®∏ Ï∑®ÏÜå
    if (computerTimerRef.current) {
      clearTimeout(computerTimerRef.current);
      computerTimerRef.current = null;
    }

    const validCombinations = findAllValidCombinations();

    if (validCombinations.length === 0) {
      // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÎäî Í≤ΩÏö∞ - Ï†êÏàò Ï∂îÍ∞Ä Î∞è Î≥¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
      setPlayerScore(prevScore => prevScore + 10);
      setToast({
        isVisible: true,
        type: 'success',
        message: 'Ï†ïÌôïÌï©ÎãàÎã§! Í∞ÄÎä•Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÏäµÎãàÎã§. +10Ï†ê'
      });

      // Î™®Îì† Ïπ¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
      const boardCardIds = board.slice(0, 9).map(card => card.id);

      // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ Î™®Îì† Ïπ¥Îìú ID Ï†ÄÏû•
      setFadingCards(boardCardIds);

      // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
      setTimeout(() => {
        // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
        setUsedCardIds(prev => [...prev, ...boardCardIds]);

        // ÏÉà ÎùºÏö¥Îìú ÏãúÏûë
        startNewRound();
      }, 600);
    } else {
      // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏûàÎäî Í≤ΩÏö∞ - Ï†êÏàò Í∞êÏ†ê Î∞è Í∞ÄÎä•Ìïú Ï°∞Ìï© ÌëúÏãú
      setPlayerScore(prevScore => Math.max(0, prevScore - 5)); // ÏµúÏÜå 0Ï†êÍπåÏßÄÎßå Í∞êÏ†ê
      setToast({
        isVisible: true,
        type: 'error',
        message: 'Í∞ÄÎä•Ìïú Ï°∞Ìï©Ïù¥ ÏûàÏäµÎãàÎã§! -5Ï†ê'
      });

      // Ï≤´ Î≤àÏß∏ Í∞ÄÎä•Ìïú Ï°∞Ìï©ÏùÑ ÌëúÏãú
      setPossibleCombinations(validCombinations);
      // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
      setSelectedCards([]);
      isProcessingRef.current = false;
    }
  };

  return (
    <div className="app">
      <h1>TRIO</h1>
      <div className="game-scores">
        <div className="player-score">
          ÌîåÎ†àÏù¥Ïñ¥: {playerScore}
        </div>
        <div className={`computer-score ${isComputerThinking ? 'thinking' : ''}`}>
          Ïª¥Ìì®ÌÑ∞: {computerScore} {isComputerThinking && 'ü§î'}
        </div>
      </div>
      <div className="game-container">
        <GameBoard 
          board={board} 
          selectedCards={selectedCards}
          computerSelectedCards={computerSelectedCards}
          possibleCombinations={possibleCombinations}
          fadingCards={fadingCards}
          isComputerThinking={isComputerThinking}
          onCardClick={handleCardSelect} 
        />
        <div className="no-team-container">
          <button 
            className="no-team-btn" 
            onClick={handleNoTeam}
            disabled={isProcessingRef.current}
          >
            NO TEAM
          </button>
        </div>
      </div>
      <Toast toast={toast} setToast={setToast} />
    </div>
  );
}

export default App;
