import React, { useState, useEffect } from 'react';
import './App.css';
import GameBoard from './components/GameBoard';
import Toast from './components/Toast';
import { initializeBoard, compareAttributes, refreshBoard } from './utils/gameLogic';
import { Card, ValidationResult, ToastMessage } from './types/types';

function App() {
  const [board, setBoard] = useState<Card[]>([]);
  const [selectedCards, setSelectedCards] = useState<Card[]>([]);
  const [validationResult, setValidationResult] = useState<ValidationResult | null>(null);
  const [score, setScore] = useState<number>(0);
  const [usedCardIds, setUsedCardIds] = useState<number[]>([]);
  const [toast, setToast] = useState<ToastMessage>({
    isVisible: false,
    type: 'success',
    message: ''
  });
  const [possibleCombinations, setPossibleCombinations] = useState<Card[][]>([]);
  const [fadingCards, setFadingCards] = useState<number[]>([]);

  useEffect(() => {
    // 보드 초기화
    setBoard(initializeBoard());
  }, []);

  // 3장의 카드가 선택될 때마다 자동으로 유효성 검증
  useEffect(() => {
    if (selectedCards.length === 3) {
      validateSelection();
    }
  }, [selectedCards]);

  const handleCardSelect = (card: Card) => {
    if (fadingCards.length > 0) return; // 카드 페이드아웃 중에는 새 카드 선택 불가

    if (selectedCards.some(c => c.id === card.id)) {
      // 이미 선택된 카드면 선택 취소
      setSelectedCards(selectedCards.filter(c => c.id !== card.id));
    } else if (selectedCards.length < 3) {
      // 3장 미만이면 선택
      setSelectedCards([...selectedCards, card]);
    }
  };

  const validateSelection = () => {
    if (selectedCards.length === 3) {
      const result = compareAttributes(selectedCards);
      setValidationResult(result);

      if (result.isValid) {
        // 유효한 조합일 경우
        setScore(prevScore => prevScore + 1);

        // 사용된 카드 ID 추가
        const selectedIds = selectedCards.map(card => card.id);
        setUsedCardIds(prev => [...prev, ...selectedIds]);

        // 토스트 메시지 표시
        setToast({
          isVisible: true,
          type: 'success',
          message: '유효한 조합입니다! +1점'
        });

        // 페이드아웃 효과를 위해 선택된 카드 ID 저장
        setFadingCards(selectedIds);

        // 타이머 설정: 페이드아웃 효과 후 새 카드로 교체
        setTimeout(() => {
          // 선택된 카드 교체
          setBoard(prevBoard => refreshBoard(prevBoard, selectedIds, usedCardIds));
          // 페이드아웃 효과 해제
          setFadingCards([]);
          // 선택 초기화
          setSelectedCards([]);
        }, 600); // 페이드아웃 애니메이션 시간에 맞춤
      } else {
        // 유효하지 않은 조합일 경우
        setToast({
          isVisible: true,
          type: 'error',
          message: '유효하지 않은 조합입니다.'
        });

        // 선택 초기화
        setSelectedCards([]);
      }
    }
  };

  // 가능한 모든 3장 조합을 확인
  const findAllValidCombinations = (): Card[][] => {
    const boardCards = board.slice(0, 9);
    const validCombinations: Card[][] = [];

    // 모든 가능한 3장 조합 생성
    for (let i = 0; i < boardCards.length; i++) {
      for (let j = i + 1; j < boardCards.length; j++) {
        for (let k = j + 1; k < boardCards.length; k++) {
          const combination = [boardCards[i], boardCards[j], boardCards[k]];
          // 유효한 조합인지 확인
          if (compareAttributes(combination).isValid) {
            validCombinations.push(combination);
          }
        }
      }
    }

    return validCombinations;
  };

  // NO TEAM 버튼 클릭 핸들러
  const handleNoTeam = () => {
    if (fadingCards.length > 0) return; // 카드 페이드아웃 중에는 NO TEAM 동작 불가

    const validCombinations = findAllValidCombinations();

    if (validCombinations.length === 0) {
      // 유효한 조합이 없는 경우 - 점수 추가 및 보드 새로고침
      setScore(prevScore => prevScore + 10);
      setToast({
        isVisible: true,
        type: 'success',
        message: '정확합니다! 가능한 조합이 없습니다. +10점'
      });

      // 모든 카드 새로고침
      const boardCardIds = board.slice(0, 9).map(card => card.id);

      // 페이드아웃 효과를 위해 모든 카드 ID 저장
      setFadingCards(boardCardIds);

      // 타이머 설정: 페이드아웃 효과 후 새 카드로 교체
      setTimeout(() => {
        setBoard(prevBoard => refreshBoard(prevBoard, boardCardIds, usedCardIds));
        setFadingCards([]);
        setSelectedCards([]);
      }, 600); // 페이드아웃 애니메이션 시간에 맞춤
    } else {
      // 유효한 조합이 있는 경우 - 점수 감점 및 가능한 조합 표시
      setScore(prevScore => Math.max(0, prevScore - 5)); // 최소 0점까지만 감점
      setToast({
        isVisible: true,
        type: 'error',
        message: '가능한 조합이 있습니다! -5점'
      });

      // 첫 번째 가능한 조합을 표시
      setPossibleCombinations(validCombinations);
      // 선택 초기화
      setSelectedCards([]);
    }
  };

  const resetSelection = () => {
    setSelectedCards([]);
    setValidationResult(null);
    setPossibleCombinations([]);
  };

  const resetGame = () => {
    setBoard(initializeBoard());
    setSelectedCards([]);
    setValidationResult(null);
    setScore(0);
    setUsedCardIds([]);
    setPossibleCombinations([]);
  };

  return (
    <div className="app">
      <h1>TRIO</h1>
      <div className="game-score">점수: {score}</div>
      <div className="game-container">
        <GameBoard 
          board={board} 
          selectedCards={selectedCards}
          possibleCombinations={possibleCombinations}
          fadingCards={fadingCards}
          onCardClick={handleCardSelect} 
        />
        <div className="no-team-container">
          <button className="no-team-btn" onClick={handleNoTeam}>NO TEAM</button>
        </div>
      </div>
      <Toast toast={toast} setToast={setToast} />
    </div>
  );
}

export default App;
