import React, { useState, useEffect, useCallback, useRef } from 'react';
import './App.css';
import GameBoard from './components/GameBoard';
import Toast from './components/Toast';
import { initializeBoard, compareAttributes, refreshBoard } from './utils/gameLogic';
import { Card, ValidationResult, ToastMessage } from './types/types';

function App() {
  const [board, setBoard] = useState<Card[]>([]);
  const [selectedCards, setSelectedCards] = useState<Card[]>([]);
  const [validationResult, setValidationResult] = useState<ValidationResult | null>(null);
  const [playerScore, setPlayerScore] = useState<number>(0);
  const [computerScore, setComputerScore] = useState<number>(0);
  const [usedCardIds, setUsedCardIds] = useState<number[]>([]);
  const [toast, setToast] = useState<ToastMessage>({
    isVisible: false,
    type: 'success',
    message: ''
  });
  const [possibleCombinations, setPossibleCombinations] = useState<Card[][]>([]);
  const [fadingCards, setFadingCards] = useState<number[]>([]);
  const [blackFadingCards, setBlackFadingCards] = useState<number[]>([]);
  const [computerSelectedCards, setComputerSelectedCards] = useState<Card[]>([]);
  const [isComputerThinking, setIsComputerThinking] = useState<boolean>(false);
  const computerTimerRef = useRef<NodeJS.Timeout | null>(null);
  const watchdogTimerRef = useRef<NodeJS.Timeout | null>(null);
  const isProcessingRef = useRef<boolean>(false);
  const lastComputerMoveTimeRef = useRef<number>(Date.now());
  const [isPaused, setIsPaused] = useState<boolean>(false);

  // Í∞ÄÎä•Ìïú Î™®Îì† 3Ïû• Ï°∞Ìï©ÏùÑ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò
  const findAllValidCombinations = useCallback((): Card[][] => {
    if (!board || board.length < 9) {
      return [];
    }

    const boardCards = board.slice(0, 9);
    const validCombinations: Card[][] = [];

    // Î™®Îì† Í∞ÄÎä•Ìïú 3Ïû• Ï°∞Ìï© ÏÉùÏÑ±
    for (let i = 0; i < boardCards.length; i++) {
      for (let j = i + 1; j < boardCards.length; j++) {
        for (let k = j + 1; k < boardCards.length; k++) {
          const combination = [boardCards[i], boardCards[j], boardCards[k]];
          // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù∏ÏßÄ ÌôïÏù∏
          if (compareAttributes(combination).isValid) {
            validCombinations.push(combination);
          }
        }
      }
    }

    return validCombinations;
  }, [board]);

  // ÏàúÏàò Ìï®ÏàòÎ°ú Ïª¥Ìì®ÌÑ∞ ÌÑ¥ Ïã§Ìñâ (ÏùòÏ°¥ÏÑ± ÏóÜÏùå)
  function executeComputerTurnFn() {
    // ÏÉÅÌÉú ÌôïÏù∏
    if (isProcessingRef.current || fadingCards.length > 0 || blackFadingCards.length > 0) {
      // 2Ï¥à ÌõÑ Îã§Ïãú ÏãúÎèÑ
      setTimeout(() => {
        if (!isProcessingRef.current && fadingCards.length === 0 && blackFadingCards.length === 0) {
          executeComputerTurnFn();
        }
      }, 2000);
      return;
    }

    try {
      // Ïª¥Ìì®ÌÑ∞ Ïã§Ìñâ ÏãúÍ∞Ñ Í∏∞Î°ù
      lastComputerMoveTimeRef.current = Date.now();

      const validCombinations = findAllValidCombinations();

      if (validCombinations.length > 0) {
        // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏûàÏùÑ Í≤ΩÏö∞ ÎûúÎç§ÏúºÎ°ú ÌïòÎÇò ÏÑ†ÌÉù
        const randomIndex = Math.floor(Math.random() * validCombinations.length);
        const selectedCombination = validCombinations[randomIndex];

        // ÏÑ∏ Ïû•Ïùò Ïπ¥ÎìúÎ•º ÌïúÍ∫ºÎ≤àÏóê ÏÑ†ÌÉù (Ïª¥Ìì®ÌÑ∞Îäî Ï¶âÏãú ÏÑ†ÌÉù)
        setComputerSelectedCards(selectedCombination);
      } else {
        // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÏùÑ Í≤ΩÏö∞ NO TEAM ÏÑ†ÌÉù
        executeComputerNoTeamFn();
      }

      setIsComputerThinking(false);
    } catch (error) {
      console.error('Ïª¥Ìì®ÌÑ∞ ÌÑ¥ Ï≤òÎ¶¨ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      // Ïò§Î•ò Î≥µÍµ¨
      isProcessingRef.current = false;
      setIsComputerThinking(false);

      // 3Ï¥à ÌõÑÏóê Îã§Ïãú ÏãúÏûë
      setTimeout(startComputerTurnFn, 3000);
    }
  }

  // ÏàúÏàò Ìï®ÏàòÎ°ú Ïª¥Ìì®ÌÑ∞ ÌÑ¥ ÏãúÏûë (ÏùòÏ°¥ÏÑ± ÏóÜÏùå)
  function startComputerTurnFn() {
    // ÏùºÏãúÏ†ïÏßÄ ÏÉÅÌÉúÍ±∞ÎÇò Ïù¥ÎØ∏ Ï≤òÎ¶¨ Ï§ëÏù¥Í±∞ÎÇò ÌéòÏù¥ÎìúÏïÑÏõÉ Ï§ëÏù¥Î©¥ Ïä§ÌÇµ
    if (isPaused || isProcessingRef.current || fadingCards.length > 0 || blackFadingCards.length > 0) {
      return false;
    }

    setIsComputerThinking(true);
    lastComputerMoveTimeRef.current = Date.now();

    // Ïª¥Ìì®ÌÑ∞Ïùò ÏùëÎãµ ÏãúÍ∞ÑÏùÑ 10~20Ï¥à ÏÇ¨Ïù¥Î°ú ÎûúÎç§ ÏÑ§Ï†ï
    const computerDelay = 10000 + Math.random() * 10000;

    // Ïù¥Ï†Ñ ÌÉÄÏù¥Î®∏Í∞Ä ÏûàÏúºÎ©¥ Ï†ïÎ¶¨
    if (computerTimerRef.current) {
      clearTimeout(computerTimerRef.current);
      computerTimerRef.current = null;
    }

    computerTimerRef.current = setTimeout(executeComputerTurnFn, computerDelay);

    return true;
  }

  // ÏàúÏàò Ìï®ÏàòÎ°ú Ïª¥Ìì®ÌÑ∞ NO TEAM Ïã§Ìñâ (ÏùòÏ°¥ÏÑ± ÏóÜÏùå)
  function executeComputerNoTeamFn() {
    if (isProcessingRef.current) return;
    isProcessingRef.current = true;

    // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÎäî Í≤ΩÏö∞ - Ï†êÏàò Ï∂îÍ∞Ä Î∞è Î≥¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
    setComputerScore(prevScore => prevScore + 10);
    setToast({
      isVisible: true,
      type: 'error',
      message: 'Ïª¥Ìì®ÌÑ∞Í∞Ä NO TEAMÏùÑ ÏÑ†ÌÉùÌñàÏäµÎãàÎã§! +10Ï†ê'
    });

    // Î™®Îì† Ïπ¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
    const boardCardIds = board.slice(0, 9).map(card => card.id);

    // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ Î™®Îì† Ïπ¥Îìú ID Ï†ÄÏû• (Í≤ÄÏùÄÏÉâ ÌéòÏù¥ÎìúÏïÑÏõÉ)
    setBlackFadingCards(boardCardIds);

    // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
    setTimeout(() => {
      try {
        // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
        setUsedCardIds(prev => [...prev, ...boardCardIds]);
        setBlackFadingCards([]);

        // ÏÉà ÎùºÏö¥Îìú ÏãúÏûë
        startNewRoundFn();
      } catch (error) {
        console.error('Ïª¥Ìì®ÌÑ∞ NO TEAM Ï≤òÎ¶¨ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        // Ïò§Î•ò Î≥µÍµ¨
        isProcessingRef.current = false;
        setBlackFadingCards([]);
        startNewRoundFn();
      }
    }, 1000);
  }

  // ÏàúÏàò Ìï®ÏàòÎ°ú ÏÉà ÎùºÏö¥Îìú ÏãúÏûë (ÏùòÏ°¥ÏÑ± ÏóÜÏùå)
  function startNewRoundFn() {
    // Ïù¥Ï†Ñ ÌÉÄÏù¥Î®∏Îì§ Ï¥àÍ∏∞Ìôî
    if (computerTimerRef.current) {
      clearTimeout(computerTimerRef.current);
      computerTimerRef.current = null;
    }

    // ÏÉàÎ°úÏö¥ Î≥¥Îìú Ï¥àÍ∏∞Ìôî
    const initialBoard = initializeBoard(usedCardIds);
    setBoard(initialBoard);

    // ÏÑ†ÌÉù ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    setSelectedCards([]);
    setComputerSelectedCards([]);
    setPossibleCombinations([]);
    setFadingCards([]);
    setBlackFadingCards([]);
    isProcessingRef.current = false;

    // Í∞úÎ∞ú ÎîîÎ≤ÑÍπÖÏö©: Ï§ëÎ≥µ Ïπ¥Îìú Ï≤¥ÌÅ¨
    const boardIds = initialBoard.map(card => card.id);
    const uniqueIds = new Set(boardIds);
    if (uniqueIds.size !== boardIds.length) {
      console.error('Ï¥àÍ∏∞ Î≥¥ÎìúÏóê Ï§ëÎ≥µÎêú Ïπ¥ÎìúÍ∞Ä ÏûàÏäµÎãàÎã§!', boardIds);
    }

    // Ïª¥Ìì®ÌÑ∞ ÌÑ¥ ÏãúÏûë
    setTimeout(startComputerTurnFn, 100);
  }

  // Ïπ¥Îìú ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handleCardSelect = useCallback((card: Card) => {
    if (fadingCards.length > 0 || blackFadingCards.length > 0 || isProcessingRef.current) return; // Ïπ¥Îìú ÌéòÏù¥ÎìúÏïÑÏõÉ Ï§ëÏù¥ÎÇò Ï≤òÎ¶¨ Ï§ëÏù¥Î©¥ ÏÑ†ÌÉù Î∂àÍ∞Ä

    if (selectedCards.some(c => c.id === card.id)) {
      // Ïù¥ÎØ∏ ÏÑ†ÌÉùÎêú Ïπ¥ÎìúÎ©¥ ÏÑ†ÌÉù Ï∑®ÏÜå
      setSelectedCards(selectedCards.filter(c => c.id !== card.id));
    } else if (selectedCards.length < 3) {
      // 3Ïû• ÎØ∏ÎßåÏù¥Î©¥ ÏÑ†ÌÉù
      setSelectedCards([...selectedCards, card]);
    }
  }, [selectedCards, fadingCards, blackFadingCards]);

  // ÌîåÎ†àÏù¥Ïñ¥ ÏÑ†ÌÉù Ïπ¥Îìú Í≤ÄÏ¶ù
  const validateSelection = useCallback(() => {
    if (selectedCards.length === 3) {
      if (isProcessingRef.current) return;
      isProcessingRef.current = true;

      // Ïù¥Ï†Ñ Ïª¥Ìì®ÌÑ∞ ÌÉÄÏù¥Î®∏ Ï∑®ÏÜå
      if (computerTimerRef.current) {
        clearTimeout(computerTimerRef.current);
        computerTimerRef.current = null;
      }

      try {
        const result = compareAttributes(selectedCards);
        setValidationResult(result);

        if (result.isValid) {
          // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïùº Í≤ΩÏö∞
          setPlayerScore(prevScore => prevScore + 1);

          // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
          const selectedIds = selectedCards.map(card => card.id);
          setUsedCardIds(prev => [...prev, ...selectedIds]);

          // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú
          setToast({
            isVisible: true,
            type: 'success',
            message: 'Ïú†Ìö®Ìïú Ï°∞Ìï©ÏûÖÎãàÎã§! +1Ï†ê'
          });

          // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ ÏÑ†ÌÉùÎêú Ïπ¥Îìú ID Ï†ÄÏû•
          setFadingCards(selectedIds);

          // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
          setTimeout(() => {
            try {
              // ÏÑ†ÌÉùÎêú Ïπ¥Îìú ÍµêÏ≤¥
              const newBoard = refreshBoard(board, selectedIds, usedCardIds);
              setBoard(newBoard);
              // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º Ìï¥Ï†ú
              setFadingCards([]);
              // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
              setSelectedCards([]);
              setComputerSelectedCards([]);
              // Ï≤òÎ¶¨ ÏÉÅÌÉú Ìï¥Ï†ú
              isProcessingRef.current = false;
              // Í∞ÄÎä•Ìïú Ï°∞Ìï© Ï¥àÍ∏∞Ìôî
              setPossibleCombinations([]);

              // Ïª¥Ìì®ÌÑ∞ Îã§Ïùå ÏÉùÍ∞Å ÏãúÏûë
              setTimeout(startComputerTurnFn, 100);

              // Í∞úÎ∞ú ÎîîÎ≤ÑÍπÖÏö©: Ï§ëÎ≥µ Ïπ¥Îìú Ï≤¥ÌÅ¨
              const newBoardIds = newBoard.map(card => card.id);
              const uniqueIds = new Set(newBoardIds);
              if (uniqueIds.size !== newBoardIds.length) {
                console.error('ÌîåÎ†àÏù¥Ïñ¥ ÌÑ¥ ÌõÑ ÏÉà Î≥¥ÎìúÏóê Ï§ëÎ≥µÎêú Ïπ¥ÎìúÍ∞Ä ÏûàÏäµÎãàÎã§!', newBoardIds);
              }
            } catch (error) {
              console.error('ÌîåÎ†àÏù¥Ïñ¥ ÏÑ†ÌÉù Ï≤òÎ¶¨ ÏôÑÎ£å Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
              // Ïò§Î•ò Î≥µÍµ¨
              isProcessingRef.current = false;
              setFadingCards([]);
              startNewRoundFn();
            }
          }, 600);
        } else {
          // Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ Ï°∞Ìï©Ïùº Í≤ΩÏö∞
          setToast({
            isVisible: true,
            type: 'error',
            message: 'Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ Ï°∞Ìï©ÏûÖÎãàÎã§.'
          });

          // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
          setSelectedCards([]);
          isProcessingRef.current = false;

          // Ïª¥Ìì®ÌÑ∞ ÌÑ¥ Îã§Ïãú ÏãúÏûë
          startComputerTurnFn();
        }
      } catch (error) {
        console.error('ÌîåÎ†àÏù¥Ïñ¥ ÏÑ†ÌÉù Í≤ÄÏ¶ù Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        // Ïò§Î•ò Î≥µÍµ¨
        setSelectedCards([]);
        isProcessingRef.current = false;
        startComputerTurnFn();
      }
    }
  }, [selectedCards.length, board, usedCardIds]);

  // Ïª¥Ìì®ÌÑ∞ ÏÑ†ÌÉù Ïπ¥Îìú Í≤ÄÏ¶ù
  const validateComputerSelection = useCallback(() => {
    if (computerSelectedCards.length === 3) {
      if (isProcessingRef.current) return;
      isProcessingRef.current = true;

      try {
        const result = compareAttributes(computerSelectedCards);

        if (result.isValid) {
          // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïùº Í≤ΩÏö∞
          const selectedIds = computerSelectedCards.map(card => card.id);

          // Í≤ÄÏùÄÏÉâ ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ ÏÑ†ÌÉùÎêú Ïπ¥Îìú ID Ï†ÄÏû•
          setBlackFadingCards(selectedIds);

          // ÏïΩÍ∞ÑÏùò ÏßÄÏó∞ ÌõÑ Ï†êÏàò Ï∂îÍ∞Ä Î∞è UI Í∞±Ïã†
          setTimeout(() => {
            setComputerScore(prevScore => prevScore + 1);

            // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú
            setToast({
              isVisible: true,
              type: 'error',
              message: 'Ïª¥Ìì®ÌÑ∞Í∞Ä Ï°∞Ìï©ÏùÑ Ï∞æÏïòÏäµÎãàÎã§! +1Ï†ê'
            });

            // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
            setUsedCardIds(prev => [...prev, ...selectedIds]);

            // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
            setTimeout(() => {
              try {
                // ÏÑ†ÌÉùÎêú Ïπ¥Îìú ÍµêÏ≤¥
                const newBoard = refreshBoard(board, selectedIds, usedCardIds);
                setBoard(newBoard);
                // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º Ìï¥Ï†ú
                setBlackFadingCards([]);
                // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
                setComputerSelectedCards([]);
                setSelectedCards([]);
                // Ï≤òÎ¶¨ ÏÉÅÌÉú Ìï¥Ï†ú
                isProcessingRef.current = false;

                // Ïª¥Ìì®ÌÑ∞ Îã§Ïùå ÏÉùÍ∞Å ÏãúÏûë
                setTimeout(startComputerTurnFn, 100);

                // Í∞úÎ∞ú ÎîîÎ≤ÑÍπÖÏö©: Ï§ëÎ≥µ Ïπ¥Îìú Ï≤¥ÌÅ¨
                const newBoardIds = newBoard.map(card => card.id);
                const uniqueIds = new Set(newBoardIds);
                if (uniqueIds.size !== newBoardIds.length) {
                  console.error('Ïª¥Ìì®ÌÑ∞ ÌÑ¥ ÌõÑ ÏÉà Î≥¥ÎìúÏóê Ï§ëÎ≥µÎêú Ïπ¥ÎìúÍ∞Ä ÏûàÏäµÎãàÎã§!', newBoardIds);
                }
              } catch (error) {
                console.error('Ïª¥Ìì®ÌÑ∞ ÏÑ†ÌÉù Ï≤òÎ¶¨ ÏôÑÎ£å Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
                // Ïò§Î•ò Î≥µÍµ¨
                isProcessingRef.current = false;
                setBlackFadingCards([]);
                startNewRoundFn();
              }
            }, 800);
          }, 1000);
        } else {
          // Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ Ï°∞Ìï©Ïùº Í≤ΩÏö∞ (Ïù¥ Í≤ΩÏö∞Îäî ÏùºÏñ¥ÎÇòÏßÄ ÏïäÏïÑÏïº Ìï®)
          console.error('Ïª¥Ìì®ÌÑ∞Í∞Ä Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ Ï°∞Ìï©ÏùÑ ÏÑ†ÌÉùÌñàÏäµÎãàÎã§:', computerSelectedCards);
          setComputerSelectedCards([]);
          isProcessingRef.current = false;
          startComputerTurnFn();
        }
      } catch (error) {
        console.error('Ïª¥Ìì®ÌÑ∞ ÏÑ†ÌÉù Í≤ÄÏ¶ù Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        // Ïò§Î•ò Î≥µÍµ¨
        isProcessingRef.current = false;
        setComputerSelectedCards([]);
        startComputerTurnFn();
      }
    }
  }, [computerSelectedCards.length, board, usedCardIds]);

  // NO TEAM Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleNoTeam = useCallback(() => {
    if (fadingCards.length > 0 || blackFadingCards.length > 0 || isProcessingRef.current) return; // Ïπ¥Îìú ÌéòÏù¥ÎìúÏïÑÏõÉ Ï§ëÏù¥ÎÇò Ï≤òÎ¶¨ Ï§ëÏù¥Î©¥ ÎèôÏûë Î∂àÍ∞Ä
    isProcessingRef.current = true;

    // Ïù¥Ï†Ñ Ïª¥Ìì®ÌÑ∞ ÌÉÄÏù¥Î®∏ Ï∑®ÏÜå
    if (computerTimerRef.current) {
      clearTimeout(computerTimerRef.current);
      computerTimerRef.current = null;
    }

    try {
      const validCombinations = findAllValidCombinations();

      if (validCombinations.length === 0) {
        // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÎäî Í≤ΩÏö∞ - Ï†êÏàò Ï∂îÍ∞Ä Î∞è Î≥¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
        setPlayerScore(prevScore => prevScore + 10);
        setToast({
          isVisible: true,
          type: 'success',
          message: 'Ï†ïÌôïÌï©ÎãàÎã§! Í∞ÄÎä•Ìïú Ï°∞Ìï©Ïù¥ ÏóÜÏäµÎãàÎã§. +10Ï†ê'
        });

        // Î™®Îì† Ïπ¥Îìú ÏÉàÎ°úÍ≥†Ïπ®
        const boardCardIds = board.slice(0, 9).map(card => card.id);

        // ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥ºÎ•º ÏúÑÌï¥ Î™®Îì† Ïπ¥Îìú ID Ï†ÄÏû•
        setFadingCards(boardCardIds);

        // ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï: ÌéòÏù¥ÎìúÏïÑÏõÉ Ìö®Í≥º ÌõÑ ÏÉà Ïπ¥ÎìúÎ°ú ÍµêÏ≤¥
        setTimeout(() => {
          try {
            // ÏÇ¨Ïö©Îêú Ïπ¥Îìú ID Ï∂îÍ∞Ä
            setUsedCardIds(prev => [...prev, ...boardCardIds]);

            // ÏÉà ÎùºÏö¥Îìú ÏãúÏûë
            startNewRoundFn();
          } catch (error) {
            console.error('NO TEAM Ï≤òÎ¶¨ ÏôÑÎ£å Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
            // Ïò§Î•ò Î≥µÍµ¨
            isProcessingRef.current = false;
            setFadingCards([]);
            startNewRoundFn();
          }
        }, 600);
      } else {
        // Ïú†Ìö®Ìïú Ï°∞Ìï©Ïù¥ ÏûàÎäî Í≤ΩÏö∞ - Ï†êÏàò Í∞êÏ†ê
        setPlayerScore(prevScore => Math.max(0, prevScore - 5)); // ÏµúÏÜå 0Ï†êÍπåÏßÄÎßå Í∞êÏ†ê
        setToast({
          isVisible: true,
          type: 'error',
          message: 'Í∞ÄÎä•Ìïú Ï°∞Ìï©Ïù¥ ÏûàÏäµÎãàÎã§! -5Ï†ê'
        });

        // ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
        setSelectedCards([]);
        isProcessingRef.current = false;

        // Ïª¥Ìì®ÌÑ∞ ÌÑ¥ Îã§Ïãú ÏãúÏûë
        startComputerTurnFn();
      }
    } catch (error) {
      console.error('NO TEAM Î≤ÑÌäº Ï≤òÎ¶¨ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      // Ïò§Î•ò Î≥µÍµ¨
      isProcessingRef.current = false;
      startComputerTurnFn();
    }
  }, [fadingCards.length, blackFadingCards.length, findAllValidCombinations, board]);

  // Ïª¥Ìì®ÌÑ∞ ÏùºÏãúÏ†ïÏßÄ/Ïû¨Í∞ú ÌÜ†Í∏Ä Ìï∏Îì§Îü¨
  const handlePauseToggle = useCallback(() => {
    setIsPaused(prev => {
      const newPausedState = !prev;

      if (newPausedState) {
        // ÏùºÏãúÏ†ïÏßÄ - ÌòÑÏû¨ ÌÉÄÏù¥Î®∏ Ï∑®ÏÜå
        if (computerTimerRef.current) {
          clearTimeout(computerTimerRef.current);
          computerTimerRef.current = null;
        }
        setIsComputerThinking(false);
      } else {
        // Ïû¨Í∞ú - Ï≤òÎ¶¨ Ï§ëÏù¥ ÏïÑÎãàÎ©¥ Îã§Ïãú ÏãúÏûë
        if (!isProcessingRef.current && fadingCards.length === 0 && blackFadingCards.length === 0) {
          startComputerTurnFn();
        }
      }

      return newPausedState;
    });
  }, [fadingCards.length, blackFadingCards.length]);

  // Ïª¥Ìì®ÌÑ∞Í∞Ä ÏùºÏ†ï ÏãúÍ∞Ñ ÎèôÏïà ÏõÄÏßÅÏù¥ÏßÄ ÏïäÏúºÎ©¥ Ïû¨ÏãúÏûëÏãúÌÇ§Îäî Í∞êÏãú Í∏∞Îä•
  useEffect(() => {
    const checkInactivity = () => {
      const now = Date.now();
      const timeSinceLastMove = now - lastComputerMoveTimeRef.current;

      // ÏùºÏãúÏ†ïÏßÄ ÏÉÅÌÉúÍ∞Ä ÏïÑÎãàÍ≥†, 60Ï¥à Ïù¥ÏÉÅ Ïª¥Ìì®ÌÑ∞Í∞Ä ÏõÄÏßÅÏù¥ÏßÄ ÏïäÏïòÏùÑ Í≤ΩÏö∞ (Ïù¥Îäî Ïò§Î•ò ÏÉÅÌÉúÎ°ú Í∞ÑÏ£º)
      if (!isPaused && isComputerThinking && timeSinceLastMove > 60000 && !isProcessingRef.current) {
        console.log('Ïª¥Ìì®ÌÑ∞Í∞Ä Ïò§Îû´ÎèôÏïà ÏùëÎãµÏù¥ ÏóÜÏñ¥ ÏûêÎèôÏúºÎ°ú Ïû¨ÏãúÏûëÌï©ÎãàÎã§.');
        if (computerTimerRef.current) {
          clearTimeout(computerTimerRef.current);
          computerTimerRef.current = null;
        }

        // Ïª¥Ìì®ÌÑ∞ ÌÑ¥ Ïû¨ÏãúÏûë
        startComputerTurnFn();
      }
    };

    // 10Ï¥àÎßàÎã§ ÎπÑÌôúÏÑ± ÏÉÅÌÉú ÌôïÏù∏
    watchdogTimerRef.current = setInterval(checkInactivity, 10000);

    return () => {
      if (watchdogTimerRef.current) {
        clearInterval(watchdogTimerRef.current);
        watchdogTimerRef.current = null;
      }
    };
  }, [isComputerThinking, isPaused]);

  // 3Ïû•Ïùò Ïπ¥ÎìúÍ∞Ä ÏÑ†ÌÉùÎê† ÎïåÎßàÎã§ ÏûêÎèôÏúºÎ°ú Ïú†Ìö®ÏÑ± Í≤ÄÏ¶ù
  useEffect(() => {
    if (selectedCards.length === 3 && !isProcessingRef.current) {
      validateSelection();
    }
  }, [selectedCards.length, validateSelection]);

  // Ïª¥Ìì®ÌÑ∞ ÏÑ†ÌÉù Ïπ¥Îìú Í≤ÄÏ¶ù
  useEffect(() => {
    if (computerSelectedCards.length === 3 && !isProcessingRef.current) {
      validateComputerSelection();
    }
  }, [computerSelectedCards.length, validateComputerSelection]);

  // Î≥¥Îìú Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    // Í≤åÏûÑ ÏãúÏûë - ÏùºÏãúÏ†ïÏßÄ ÏÉÅÌÉúÍ∞Ä ÏïÑÎãê ÎïåÎßå Ïª¥Ìì®ÌÑ∞ ÌÑ¥ ÏãúÏûë
    startNewRoundFn();

    // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú ÌÉÄÏù¥Î®∏ Ï†ïÎ¶¨
    return () => {
      if (computerTimerRef.current) {
        clearTimeout(computerTimerRef.current);
        computerTimerRef.current = null;
      }
      if (watchdogTimerRef.current) {
        clearInterval(watchdogTimerRef.current);
        watchdogTimerRef.current = null;
      }
    };
  }, []);

  return (
    <div className="app">
      <h1>TRIO</h1>
      <div className="game-controls">
        <div className="game-scores">
          <div className="player-score">
            ÌîåÎ†àÏù¥Ïñ¥: {playerScore}
          </div>
          <div className="computer-score">
            Ïª¥Ìì®ÌÑ∞: {computerScore} {isComputerThinking && !isPaused && 'ü§î'}
          </div>
        </div>
        <button 
          className={`pause-btn ${isPaused ? 'paused' : ''}`}
          onClick={handlePauseToggle}
        >
          {isPaused ? 'Ïª¥Ìì®ÌÑ∞ Ïû¨Í∞ú' : 'Ïª¥Ìì®ÌÑ∞ ÏùºÏãúÏ†ïÏßÄ'}
        </button>
      </div>
      <div className="game-container">
        <GameBoard 
          board={board}
          selectedCards={selectedCards}
          computerSelectedCards={computerSelectedCards}
          possibleCombinations={[]}
          fadingCards={fadingCards}
          blackFadingCards={blackFadingCards}
          isComputerThinking={isComputerThinking}
          onCardClick={handleCardSelect}
        />
        <div className="no-team-container">
          <button
            className="no-team-btn"
            onClick={handleNoTeam}
            disabled={fadingCards.length > 0 || blackFadingCards.length > 0 || isProcessingRef.current}
          >
            Ï°∞Ìï© ÏóÜÏùå
          </button>
        </div>
      </div>
      <Toast toast={toast} setToast={setToast} />
    </div>
  );
}

export default App;
